// مؤثرات تفاعلية جذابة للبنات الكاشير
document.addEventListener('DOMContentLoaded', function() {
    
    // إضافة مؤثرات النقر مع الأصوات البصرية
    const buttons = document.querySelectorAll('.shortcut');
    
    buttons.forEach((button, index) => {
        // مؤثر النقر مع توهج
        button.addEventListener('click', function(e) {
            // إنشاء مؤثر التوهج
            const ripple = document.createElement('div');
            ripple.style.position = 'absolute';
            ripple.style.borderRadius = '50%';
            ripple.style.background = 'rgba(255, 255, 255, 0.6)';
            ripple.style.transform = 'scale(0)';
            ripple.style.animation = 'ripple 0.6s linear';
            ripple.style.left = (e.clientX - button.offsetLeft) + 'px';
            ripple.style.top = (e.clientY - button.offsetTop) + 'px';
            ripple.style.width = ripple.style.height = '20px';
            ripple.style.pointerEvents = 'none';
            
            button.style.position = 'relative';
            button.appendChild(ripple);
            
            // إزالة المؤثر بعد انتهاء الأنيميشن
            setTimeout(() => {
                ripple.remove();
            }, 600);
            
            // مؤثر الاهتزاز
            button.style.animation = 'shake 0.5s ease-in-out';
            setTimeout(() => {
                button.style.animation = '';
            }, 500);
        });
        

        
        // مؤثرات إضافية عند التمرير
        button.addEventListener('mouseenter', function() {
            this.style.transform = 'scale(1.05) translateY(-5px)';
            this.style.zIndex = '10';
            
            // توهج الأيقونة
            const icon = this.querySelector('.shortcut-icon');
            if (icon) {
                icon.style.animation = 'iconGlow 0.8s infinite alternate';
            }
        });
        
        button.addEventListener('mouseleave', function() {
            // إعادة المربع لحالته الأولية
            this.style.transform = '';
            this.style.zIndex = '';
            this.style.boxShadow = '';
            this.style.borderColor = '';
            this.style.background = '';
            this.style.color = '';
            
            // إيقاف توهج الأيقونة وإعادة الصورة لحجمها الطبيعي
            const icon = this.querySelector('.shortcut-icon');
            if (icon) {
                icon.style.animation = '';
                icon.style.transform = '';
                icon.style.filter = '';
                icon.style.background = '';
            }
            
            // إعادة العنوان لحالته الأولية
            const title = this.querySelector('.shortcut-title');
            if (title) {
                title.style.transform = '';
                title.style.color = '';
            }
        });
    });
    

});



// إضافة CSS للأنيميشن
const style = document.createElement('style');
style.textContent = `
    @keyframes ripple {
        to {
            transform: scale(4);
            opacity: 0;
        }
    }
    
    @keyframes shake {
        0%, 100% { transform: translateX(0); }
        25% { transform: translateX(-5px); }
        75% { transform: translateX(5px); }
    }
    

    
    .shortcut {
        transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    }
    
    .shortcut:hover {
        transform: scale(1.05) translateY(-5px) !important;
    }
`;

document.head.appendChild(style);

// مؤثرات إضافية للتفاعل
document.addEventListener('mousemove', function(e) {
    const buttons = document.querySelectorAll('.shortcut');
    buttons.forEach(button => {
        const rect = button.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        
        if (x > 0 && x < rect.width && y > 0 && y < rect.height) {
            button.style.transform = `scale(1.05) translateY(-5px) perspective(1000px) rotateX(${(y - rect.height/2)/10}deg) rotateY(${(x - rect.width/2)/10}deg)`;
            
            // توهج الأيقونة حسب موقع الماوس
            const icon = button.querySelector('.shortcut-icon');
            if (icon) {
                const iconRect = icon.getBoundingClientRect();
                const iconX = e.clientX - iconRect.left;
                const iconY = e.clientY - iconRect.top;
                
                icon.style.transform = `scale(1.1) rotate(5deg) perspective(500px) rotateX(${(iconY - iconRect.height/2)/20}deg) rotateY(${(iconX - iconRect.width/2)/20}deg)`;
                icon.style.background = `radial-gradient(circle at ${iconX}px ${iconY}px, rgba(255, 165, 0, 0.3), rgba(255, 165, 0, 0.1)) !important`;
            }
        }
    });
});

// مؤثرات النقر مع الألوان
document.querySelectorAll('.shortcut').forEach(button => {
    button.addEventListener('click', function() {
        // تغيير اللون مؤقتاً
        const originalBackground = this.style.background;
        this.style.background = 'linear-gradient(45deg, #fff, #f0f0f0)';
        this.style.color = '#333';
        
        setTimeout(() => {
            this.style.background = originalBackground;
            this.style.color = 'white';
        }, 300);
    });
});

style:
/* تنسيق عام للصفحة */
body {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    font-family: 'Cairo', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    margin: 0;
    padding: 0;
    min-height: 100vh;
}

/* تنسيق الحاوية الرئيسية */
.container {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border-radius: 25px;
    margin: 20px auto;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

/* تنسيق الاختصارات */
.shortcut {
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    display: block;
    background: rgba(255, 255, 255, 0.1);
    border: 3px solid #ffa500;
    border-radius: 20px;
    padding: 20px 15px;
    margin: 0;
    box-shadow: 0 8px 25px rgba(255, 165, 0, 0.2);
    position: relative;
    overflow: hidden;
    text-decoration: none;
    color: #333;
    font-weight: bold;
    text-align: center;
    height: 100%;
    min-height: 150px;
}

.shortcut::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.5s;
}

.shortcut:hover {
    transform: scale(1.05) translateY(-5px);
    box-shadow: 0 15px 35px rgba(255, 215, 0, 0.6);
    border-color: #ffd700;
    background: rgba(255, 255, 255, 0.15);
    text-decoration: none;
    color: #333;
}

.shortcut:hover::before {
    left: 100%;
}

.shortcut:active {
    transform: scale(0.95);
}

/* تنسيق الأيقونات - بدون خلفية أو تلوين */
.shortcut-icon {
    transition: all 0.3s ease;
    background: transparent !important;
    border: none !important;
    outline: none !important;
    width: 65px;
    height: 60px;
    margin-bottom: 10px;
    filter: none;
    mix-blend-mode: normal;
    object-fit: contain;
}

.shortcut:hover .shortcut-icon {
    transform: scale(1.1);
    filter: none;
    transition: all 0.3s ease;
}

/* تنسيق العناوين */
.shortcut-title {
    margin: 10px 0 0 0;
    font-size: 14px;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
    transition: all 0.3s ease;
    font-weight: 600;
}

.shortcut:hover .shortcut-title {
    transform: scale(1.05);
    color: #333;
}

/* مؤثرات إضافية للاختصارات المختلفة - ألوان برتقالية خفيفة */
.shortcut:nth-child(1) {
    border-color: #ffa500;
}

.shortcut:nth-child(2) {
    border-color: #ffb84d;
}

.shortcut:nth-child(3) {
    border-color: #ffc266;
}

.shortcut:nth-child(4) {
    border-color: #ffcc80;
}

.shortcut:nth-child(5) {
    border-color: #ffd699;
}

.shortcut:nth-child(6) {
    border-color: #ffe0b3;
}

.shortcut:nth-child(7) {
    border-color: #ffebcc;
}

.shortcut:nth-child(8) {
    border-color: #fff5e6;
}



/* مؤثرات التوهج */
@keyframes glow {
    0% { box-shadow: 0 8px 25px rgba(255, 165, 0, 0.3); }
    50% { box-shadow: 0 8px 25px rgba(255, 165, 0, 0.6); }
    100% { box-shadow: 0 8px 25px rgba(255, 165, 0, 0.3); }
}

/* أنيميشن النمل الذهبي المتوهج */
@keyframes antBorder {
    0% {
        border-image: linear-gradient(45deg, #ffd700, #ffed4e, #ffd700, #ffed4e) 1;
        border-image-slice: 1;
    }
    25% {
        border-image: linear-gradient(135deg, #ffd700, #ffed4e, #ffd700, #ffed4e) 1;
        border-image-slice: 1;
    }
    50% {
        border-image: linear-gradient(225deg, #ffd700, #ffed4e, #ffd700, #ffed4e) 1;
        border-image-slice: 1;
    }
    75% {
        border-image: linear-gradient(315deg, #ffd700, #ffed4e, #ffd700, #ffed4e) 1;
        border-image-slice: 1;
    }
    100% {
        border-image: linear-gradient(45deg, #ffd700, #ffed4e, #ffd700, #ffed4e) 1;
        border-image-slice: 1;
    }
}



/* تحسين التجاوب */
@media (max-width: 1200px) {
    .shortcut {
        min-height: 140px;
        padding: 18px 12px;
    }
    
    .shortcut-icon {
        width: 60px;
        height: 55px;
    }
}

@media (max-width: 900px) {
    .shortcut {
        min-height: 130px;
        padding: 15px 10px;
    }
    
    .shortcut-icon {
        width: 55px;
        height: 50px;
    }
    
    .shortcut-title {
        font-size: 13px;
    }
}

@media (max-width: 600px) {
    .container {
        margin: 10px;
        padding: 15px !important;
    }
    
    .shortcut {
        min-height: 120px;
        padding: 12px 8px;
    }
    
    .shortcut-icon {
        width: 50px;
        height: 45px;
        margin-bottom: 8px;
    }
    
    .shortcut-title {
        font-size: 12px;
        margin: 8px 0 0 0;
    }
}

@media (max-width: 400px) {
    .shortcut {
        min-height: 110px;
        padding: 10px 5px;
    }
    
    .shortcut-icon {
        width: 45px;
        height: 40px;
    }
    
    .shortcut-title {
        font-size: 11px;
    }
}
