style:
/* تنسيق الحاوية الرئيسية */
.container {
  background: #fff;
  border-radius: 8px;
  margin: 20px auto;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
}

/* تنسيق الاختصارات */
.shortcut {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #fff;
  border: 2px solid #ff9800;
  border-radius: 12px;
  padding: 15px 10px;
  text-decoration: none;
  color: #333;
  text-align: center;
  min-height: 110px;
  height: 100%;
  transition: transform 0.2s, box-shadow 0.2s;
}

.shortcut:hover {
  transform: translateY(-4px);
  box-shadow: 0 4px 12px rgba(255, 152, 0, 0.3);
  text-decoration: none;
}

.shortcut:active {
  transform: translateY(-2px);
}

/* تنسيق الأيقونات */
.shortcut-icon {
  width: 50px;
  height: 50px;
  margin-bottom: 8px;
  object-fit: contain;
}

/* تنسيق العناوين */
.shortcut-title {
  margin: 8px 0 0 0;
  font-size: 13px;
  font-weight: 600;
  color: #333;
  line-height: 1.3;
}

/* ألوان مختلفة للبطاقات */
.shortcut:nth-child(1) {
  border-color: #ff6f00;
}
.shortcut:nth-child(2) {
  border-color: #ff8f00;
}
.shortcut:nth-child(3) {
  border-color: #ffa726;
}
.shortcut:nth-child(4) {
  border-color: #ffb74d;
}
.shortcut:nth-child(5) {
  border-color: #ffca28;
}
.shortcut:nth-child(6) {
  border-color: #ffd54f;
}
.shortcut:nth-child(7) {
  border-color: #ffe082;
}
.shortcut:nth-child(8) {
  border-color: #ffecb3;
}

/* تحسين التجاوب */
@media (max-width: 768px) {
  .shortcut {
    padding: 15px 10px;
  }
  .shortcut-icon {
    width: 50px;
    height: 50px;
  }
  .shortcut-title {
    font-size: 13px;
  }
}

@media (max-width: 480px) {
  .shortcut {
    padding: 12px 8px;
  }
  .shortcut-icon {
    width: 40px;
    height: 40px;
  }
  .shortcut-title {
    font-size: 12px;
  }
}
